  --shadow: 0px 1px 2px 0px hsl(0 0% 0% / 0.12), 0px 2px 4px 0px hsl(0 0% 0% / 0.20);
  --shadow-md: 0px 2px 4px -1px hsl(0 0% 0% / 0.18), 0px 4px 6px -1px hsl(0 0% 0% / 0.24);
  --shadow-lg: 0px 4px 6px -2px hsl(0 0% 0% / 0.16), 0px 10px 15px -3px hsl(0 0% 0% / 0.28);
  --shadow-xl: 0px 10px 10px -5px hsl(0 0% 0% / 0.14), 0px 20px 25px -5px hsl(0 0% 0% / 0.30);
